<div class="panel panel-primary module">
	<div class="panel-heading">
		<h2>Komunitas</h2>
	</div>
	<div class="panel-body">
		<form action="?url=auth/login" method="post">
			<!-- Form -->
			<input type="hidden" name="login-member">
			<div class="form">
				<div class="form-group">
				<input type="text" name="id" placeholder="Email" class="form-control">
				</div>
				<div class="form-group">
				<input type="password" name="password" placeholder="Password" class="form-control">
				</div>
				<span><a href="?url=index/pendaftaranmember1">Anggota yang sudah terdaftar?</a></span>
				<!-- Form Buttons -->
				<div class="buttons">
				  <input type="submit" class="button" value="Login" />
				</div>
				<!-- End Form Buttons -->
			</div>
			<!-- End Form -->
		</form>
	</div>
</div>
